package com.swamedia.learn.wsjersey.model;

public class Nilai {
	private Integer nrp;
	private Integer mkID;
	private int nilai;

	public Nilai() {
		// TODO Auto-generated constructor stub
	}

	public Integer getNrp() {
		return nrp;
	}

	public void setNrp(Integer nrp) {
		this.nrp = nrp;
	}

	public Integer getMkID() {
		return mkID;
	}

	public void setMkID(Integer mkID) {
		this.mkID = mkID;
	}

	public int getNilai() {
		return nilai;
	}

	public void setNilai(int nilai) {
		this.nilai = nilai;
	}

}
